mapper.select:
select * from(
select * from(
	select rownum as rn, b.* from ( (select * from music order by mseq desc) b )
) where rn >= #{startNum}
) where rn <= #{endNum}

select * from(
select * from(
	select rownum as rn, m.* from 
	( (select * from member where name like '%'||#{param2}||'%' order by indate desc) m )
) where rn >= #{param1.startNum}
) where rn <= #{param1.endNum}
