mapper.select:
select * from(
select * from(
	select rownum as rn, b.* from 
	( (select * from artist_view where name like '%'||#{search.searchKeyword}||'%' order by atseq desc) b )
) where rn >= #{paging.startNum}
) where rn <= #{paging.endNum}

mapper.update:
update artist
	set
		name = #{name}
		, groupyn = #{groupyn}
		, gender = #{gender}
		, gseq = #{gseq}
		, img = #{img}
		, description = #{description}
where atseq = #{atseq}

mapper.insert:
insert into artist(
	atseq
	, name
	, groupyn
	, gender
	, gseq
	, img
	, description
) values(
	artist_seq.nextval
	, #{name}
	, #{groupyn}
	, #{gender}
	, #{gseq}
	, #{img, jdbcType=VARCHAR}
	, #{description}
)

	select artist_seq.currval as atseq from dual
